con:configuration.scriptText:
import groovy.json.JsonSlurper

log.info(messageExchange.response.responseContent[0]);

def ResponseMessage = messageExchange.response.responseContent

// replace "(starting&ending), [](starting&ending) and \ with space from response
def TrimResponse = ResponseMessage.replaceAll('^\"|\"$','').replaceAll('^\\[|\\]$','')replaceAll('\\\\','')

//define a JsonSlurper
def jsonSlurper = new JsonSlurper().parseText(TrimResponse)

assert jsonSlurper.name != null

assert jsonSlurper.name instanceof String

import groovy.json.JsonSlurper

log.info(messageExchange.response.responseContent[0]);

def ResponseMessage = messageExchange.response.responseContent

// replace "(starting&ending), [](starting&ending) and \ with space from response
def TrimResponse = ResponseMessage.replaceAll('^\"|\"$','').replaceAll('^\\[|\\]$','')replaceAll('\\\\','')

//define a JsonSlurper
def jsonSlurper = new JsonSlurper().parseText(TrimResponse)

assert jsonSlurper.surname != null

assert jsonSlurper.surname instanceof String

